        {
            id: 1,
            name: 'first',
        },
        {
            id: 2,
            name: 'second',
        },
        {
            id: 3,
            name: 'third',
        },
    ]);
    const [draggingItem, setDraggingItem] = useState(null);

    const handleDragStart = (e, item) => {
        setDraggingItem(item);
        e.dataTransfer.setData('text/plain', '');
    };

    const handleDragEnd = () => {
        setDraggingItem(null);
