import React, { useState } from 'react';

export const UploadComponent = () => {
    const [file, setFile] = useState(null);
    const handleFileChange = (event) => {
        setFile(event.target.files[0]);
        console.log(file);
        
    }

    const handleSubmit = async (event) => {
        event.preventDefault();
        var json ={
            "id": 1,
            "name": 'firstasdjkahsaskakskdjasjkdjkakjsssssssssssssskjskkdasd aasdasdakslk',
            "positionx": null,
            "positiony": null
        }
        const formData = new FormData();
        console.log(file);
        formData.append('asdf', JSON.stringify(json));
        console.log(formData.getAll('asdf'));
        try {
            const res = await fetch("http://localhost:8000/getfile", {
                method: 'PUT',
                headers:{
                    'Content-Type': 'application/json'
                },
                body: {value : "hi"}
                
            });
            
            console.log(res.ok);
        }
        catch (error){
            console.error('upload error');
        }
    }
    return (
        <form onSubmit = {handleSubmit}>
            <input type = "file" accept = ".txt, .java, .py" onChange = {handleFileChange} />
            <button type = "submit">Upload</button>
        </form>
    );
  };

export const ReadComponent = () => {
    const [file, setFile] = useState(null);
    const handleFileChange = (event) => {
        setFile(event.target.files[0]);
        console.log(file);
    }


    const fileReader = new FileReader();
    let txt = fileReader.readAsText(file);
    
    console.log(txt);
}
