import { useState } from 'react';
import Button from 'react-bootstrap/Button';
import Form from 'react-bootstrap/Form';
import Modal from 'react-bootstrap/Modal';

export function texttoJSON(){
    let uploadtext = selectedFile.text();
    let json = "";
    let i = 0;
    while (uploadtext.includes('\\')){
      
      json += '{"id": "'+i+'", "name": "'+ uploadtext.substring(0, indexOf('\\')) + '"}';
      uploadtext = uploadtext.substring(findIndex("\\")+2);
      i++;
    }
return (
  {json}
);
}

export function PopUp() {
  //hooks - makes the popup 'appear' 
  const [show, setShow] = useState(false);

  const handleClose = () => setShow(false);
  const handleShow = () => setShow(true);

  const [selectedFile, setSelectedFile] = useState(null);
  // use selectedFile to access file
  const handleFileChange = (event) => {
    setSelectedFile(event.target.files[0]);
  }

  const handleSubmit = (event) => {
    event.preventDefault();
    console.log(selectedFile.text());
  }
  
  return (
    <>
      <button variant="primary" onClick={handleShow}>
        Import a Problem
      </button>

      <Modal
        show={show}
        onHide={handleClose}
        backdrop="static"
        keyboard={false}
        
        centered = {true}
      >
        <Modal.Header closeButton>
          <Modal.Title>Upload</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <form onSubmit = {handleSubmit}>
            <input type = "file" accept = ".txt, .java, .py" onChange={handleFileChange}/>
            <button type = "submit">Upload</button>
          </form>
        </Modal.Body>
        <Modal.Footer>
          <Button variant="secondary" onClick={handleClose}>
            Close
          </Button>
            
        </Modal.Footer>
      </Modal>
    </>
  );
}
